using System; using System.Threading.Tasks; using Xamarin.Forms; using LogInMekashron.Services; using System.Xml.Linq;
using System.Linq;

namespace LogInMekashron.Dialog {     public class DialogService : IDialogService     {         private Page _dialogPage;          public DialogService(Page dialogPage)         {             _dialogPage = dialogPage;         }          public Task ShowMessage(XDocument soap)         {             string message = (from article in soap.Descendants("article")                               select new                               {                                   message = article.Descendants("ResultMessage").SingleOrDefault()                               }).ToString();              return _dialogPage.DisplayAlert("Message", message, "OK");         }     } }